const body=$("body"),modal=$("#modal"),modalTrigger=$("[data-modal]"),modalContent=$(".modal-content");function closeModal(){body.removeClass("modal-open")}function openModal(o){body.addClass("modal-open")}function launchModal(o,l){let a=o.attr("href"),d=o.data("location"),n=a.replace("#","");modalContent.load("/partials/"+d+"/"+n+".html",function(){openModal(modal),null!==l&&l()})}$(".modal-screen, .close-modal").click(function(){closeModal()}),$(document).keyup(function(o){27===o.keyCode&&closeModal()});