const body=$("body"),modal=$("#modal"),modalTrigger=$("[data-modal]"),modalWrapper=$(".modal-wrapper"),modalContent=$(".modal-content");function closeModal(){body.removeClass("modal-open")}function openModal(o){body.addClass("modal-open")}function launchModal(o,a){let l=o.attr("href"),d=o.data("location"),n=l.replace("#","");modalContent.load("_partials/"+d+"/"+n,function(){openModal(modal),null!==a&&a()})}$(".modal-screen, .close-modal").click(function(){closeModal()}),$(document).keyup(function(o){27===o.keyCode&&closeModal()});