"use strict";var setPassword="intuitive",homepageUrl="http://hgapoly.test/home";function setCookie(e,t,o){var n=new Date;n.setTime(n.getTime()+60*o*60*1e3);var i="expires="+n.toUTCString();document.cookie=e+"="+t+"; "+i}function getCookie(e){for(var t=e+"=",o=document.cookie.split(";"),n=0;n<o.length;n++){for(var i=o[n];" "==i.charAt(0);)i=i.substring(1);if(-1!=i.indexOf(t))return i.substring(t.length,i.length)}return null}function isValid(){document.getElementById("password").value==setPassword?setCookie("hgapoly","seen",1):(alert("Sorry, that is the wrong password."),event.preventDefault())}window.onload=function(){"seen"==getCookie("hgapoly")&&(window.location.href=homepageUrl)};