"use strict";var setPassword="poly",homepageUrl="http://hgapoly.test/home";function setCookie(e,t,o){var n=new Date;n.setTime(n.getTime()+60*o*60*1e3);var r="expires="+n.toUTCString();document.cookie=e+"="+t+"; "+r}function getCookie(e){for(var t=e+"=",o=document.cookie.split(";"),n=0;n<o.length;n++){for(var r=o[n];" "==r.charAt(0);)r=r.substring(1);if(-1!=r.indexOf(t))return r.substring(t.length,r.length)}return null}function isValid(){document.getElementById("password").value==setPassword?setCookie("hgapoly","seen",1):(alert("Sorry, that is the wrong password."),event.preventDefault())}window.onload=function(){"seen"==getCookie("hgapoly")&&(window.location.href=homepageUrl)};